contract = web3.eth.contract(address=contract_address, abi=contract_abi)


def multi_swap(file_name):
    # 读取地址信息
    with open(file_name, 'r') as file:
        # 从文件中加载JSON数据
        wallet_dictionary = json.load(file)
        for wallet in wallet_dictionary:
            transfer_address = wallet['address']
            # 接收钱包地址
            recipient = wallet['address']
            amount = web3.to_wei(0.03, 'ether')
            nonce = web3.eth.get_transaction_count(transfer_address)
            print(f"{transfer_address} 准备转账")

            swap_params = {
                'from': transfer_address,
                'amountIn': amount,
                'amountOutMin': 0,
                'path': [bnb_contract_address, busd_contract_address],
                'to': recipient,
                'deadline': int(time.time()) + 300  # 5 minutes from now
            }

            pancake_swap_txn = contract.functions.swapExactETHForTokens(
                30000000000000000,
                swap_params['amountOutMin'],
                swap_params['path'],
                swap_params['to'],
                swap_params['deadline']
            ).build_transaction({'nonce': nonce, 'gasPrice': web3.to_wei('5', 'gwei'), 'gas': 2500000})

            signed_txn = web3.eth.account.sign_transaction(pancake_swap_txn, private_key=wallet['privateKey'])
            tx_token = web3.eth.send_raw_transaction(signed_txn.rawTransaction)
            tx_hash = web3.to_hex(tx_token)
            print(tx_hash)
            print(f"{transfer_address} 已转换 {amount} 个BNB")
            print()

multi_swap('2024-1-11_mnemonic_address_bot.json')